class ToggleLike{constructor(t){this.toggler=t,this.toggleLike()}toggleLike(){$(this.toggler).click((function(t){t.preventDefault();let e=this,l=$(e).attr("href");fetch(`http://localhost:8000/${l}`,{method:"POST"}).then((t=>t.json())).then((t=>{let l=parseInt($(e).attr("data-likes"));t.data.deleted?l-=1:l+=1,$(e).attr("data-likes",l),$(e).html(`${l} Likes`)})).catch((t=>console.error(t)))}))}}